class Decode {
    /**
     * * Write a function that takes a message, and decodes in such a 
     * way that it swaps case of all letters, replaces all vowels in 
     * the message with the letter that appears 2 places ahead of that 
     * vowel in the english alphabet. 
     * Assume only letters. 
     * 
     * Examples:
     * >>> decode('test')
     * 'TEST'
     * >>> decode('This is a message')
     * 'THIS IS A MESSAGE'
     *
     */
    public static String decode(String message) {
		String vowels = "aeiou";
		String decoded = "";
		for (int i = 0; i < message.length(); i++) {
			char c = message.charAt(i);
			if (vowels.indexOf(c) != -1) {
				int index = (vowels.indexOf(c) + 3) % 5;
				decoded += vowels.charAt(index);
			} else {
				decoded += (c == c.toUpperCase()) ? Character.toLowerCase(c) : Character.toUpperCase(c);
			}
		}
		return decoded;
    }
}


