class LargestDivisor {
    /**
     * For a given number n, find the largest number that divides n evenly, smaller than n
     * >>> largest_divisor(15)
     * 5
     *
     */
    public static int largestDivisor(int n) {
		int i = 1;
		while (i <= n) {
			if (n % i == 0) {
				return i;
			}
			i++;
		}
		return -1;
    }
}
