class IsPrime {
    /**
     * Return true if a given number is prime, and false otherwise.
     * >>> is_prime(6)
     * False
     * >>> is_prime(101)
     * True
     * >>> is_prime(11)
     * True
     * >>> is_prime(13441)
     * True
     * >>> is_prime(61)
     * True
     * >>> is_prime(4)
     * False
     * >>> is_prime(1)
     * False
     *
     */
    public static Boolean isPrime(int n) {
		if (n <= 1) {
			return false;
		}
		for (int i = 2; i <= Math.sqrt(n); i++) {
			if (n % i == 0) {
				return false;
			}
		}
		return true;
    }
}
