import java.io.*;
import java.lang.*;
import java.util.*;
import java.math.*;


class IsPalindrome {
    /**
     * * Checks if given string is a palindrome
     * >>> is_palindrome('')
     * True
     * >>> is_palindrome('aba')
     * True
     * >>> is_palindrome('aaaaa')
     * True
     * >>> is_palindrome('zbcd')
     * False
     *
     */
    public static Boolean isPalindrome(String text) {
		if (text.length() == 0) {
			return true;
		}
		if (text.length() == 1) {
			return true;
		}
		if (text.charAt(0) == text.charAt(text.length() - 1)) {
			return isPalindrome(text.substring(1, text.length() - 1));
		}
