import java.math.*;


class Modp {
    /**
     * Return 2^n modulo p (be aware of numerics).
     * >>> modp(3, 5)
     * 3
     * >>> modp(1101, 101)
     * 2
     * >>> modp(0, 101)
     * 1
     * >>> modp(3, 11)
     * 8
     * >>> modp(100, 101)
     * 1
     *
     */
    public static int modp(int n, int p) {
		BigInteger b = BigInteger.valueOf(n);
		BigInteger c = BigInteger.valueOf(p);
		return b.mod(c).intValue();
    }
}
