import java.io.*;
import java.lang.*;
import java.util.*;
import java.math.*;


class Common {
    /**
     * Return sorted unique common elements for two lists.
     * >>> common([1, 4, 3, 34, 653, 2, 5], [5, 7, 1, 5, 9, 653, 121])
     * [1, 5, 653]
     * >>> common([5, 3, 2, 8], [3, 2])
     * [2, 3]

     *
     */
    public static List<Object> common(List<Integer> l1, List<Object> l2) {
		List<Object> result = new ArrayList<Object>();
		for (Object o : l1) {
			if (l2.contains(o)) {
				result.add(o);
			}
		}
