			if (hex.charAt(i) == '2' || hex.charAt(i) == '3' || hex.charAt(i) == '5' || hex.charAt(i) == '7' || hex.charAt(i) == 'B' || hex.charAt(i) == 'D') {
				count++;
			}
		}
		return count;
    }
