    /**
     * * You are given a list of integers.
     * Write a function next_smallest() that returns the 2nd smallest element of the list.
     * Return null if there is no such element.
     * 
     * next_smallest([1, 2, 3, 4, 5]) == 2
     * next_smallest([5, 1, 4, 3, 2]) == 2
     * next_smallest([]) == None
     * next_smallest([1, 1]) == None
     *
     */
    public static Integer nextSmallest(List<Object> lst) {
		if (lst.size() < 2) {
			return null;
		}
		Integer min = (Integer) lst.get(0);
		Integer secMin = (Integer) lst.get(1);
		for (int i = 0; i < lst.size(); i++) {
			if (lst.get(i) instanceof Integer) {
				if ((Integer) lst.get(i) < min) {
					secMin = min;
					min = (Integer) lst.get(i);
				} else if ((Integer) lst.get(i) < secMin) {
					secMin = (Integer) lst.get(i);
				}
			}
		}
		return secMin;
    }
}
