    /**
     * Given an array, find the int that appears an odd number of times.
     * There will always be only one integer that appears an odd number of times.
     */
    public static int findIt(int[] seq) {
        Arrays.sort(seq);
        int result = seq[0];
        int count = 1;
        for (int i = 1; i < seq.length; i++) {
            if (seq[i] == result) {
                count++;
            } else {
                if (count % 2 == 1) {
                    return result;
                }
                result = seq[i];
                count = 1;
            }
        }
        if (count % 2 == 1) {
            return result;
        }
        return 0;
    }
}
