     *
     */
    public static List<String> oddCount(List<String> lst) {
		List<String> result = new ArrayList<String>();
		for (int i = 0; i < lst.size(); i++) {
			int count = 0;
			for (int j = 0; j < lst.get(i).length(); j++) {
				if (Integer.parseInt(lst.get(i).substring(j, j + 1)) % 2!= 0) {
					count++;
				}
			}
