"""
Write a function to find out the maximum sum such that no two chosen numbers are adjacent for the given rectangular grid of dimension 2 x n.
"""
def max_sum_rectangular_grid(grid, n) : 

    # Initialize result 
    res = 0; 

    # Compute sum of all rows 
    for i in range(n) : 
        curr_sum = 0; 
        for j in range(n) : 
            curr_sum += grid[i][j]; 
        res = max(res, curr_sum); 

    # Compute sum of all columns 
    for i in range(n) : 
        curr_sum = 0; 
        for j in range(n) : 
            curr_sum += grid[j][i]; 
        res = max(res, curr_sum); 

    # Compute sum of all diagonals 
    for i in range(n) : 
        curr_sum = 0; 
        for j in range(n) : 
            curr_sum += grid[i][j]; 
        res = max(res, curr_sum); 

    # Compute sum of all anti-diagonals 
    for i in range(n) : 
        curr_sum = 0; 
        for j in range(n) : 
            curr_sum += grid[i][j]; 
        res = max(res, curr_sum); 

    return res; 

