class FindMissingNumber {
    /**
     * Given an array of integers where each integer represents a different
     * position in the array, find the first missing integer in the array.
     *
     * Example:
     * >>> find_missing_number([3, 2, 0, 4])
     * 1
     *
     * The integer 1 is missing from the array.
     *
     */
    public static int findMissingNumber(int[] numbers) {
		int i = 0;
		while (i < numbers.length) {
			if (numbers[i]!= i && numbers[i] >= 0 && numbers[i] < numbers.length && numbers[i]!= numbers[numbers[i]]) {
				int temp = numbers[i];
				numbers[i] = numbers[temp];
				numbers[temp] = temp;
			} else {
				i += 1;
			}
		}
		for (i = 0; i < numbers.length; i++) {
			if (numbers[i]!= i) {
				return i;
			}
		}
		return numbers.length;
    }
}
