     */
    public static List<Integer> sortEven(List<Integer> l) {
		List<Integer> even = new ArrayList<Integer>();
		for (int i = 0; i < l.size(); i++) {
			if (i % 2 == 0) {
				even.add(l.get(i));
			}
		}
		Collections.sort(even);
		for (int i = 0; i < l.size(); i++) {
			if (i % 2 == 0) {
				l.set(i, even.get(i/2));
			}
		}
		return l;
    }
}
