class PrimeFib {
    /**
     * * prime_fib returns n-th number that is a Fibonacci number and it's also prime.
     * >>> prime_fib(1)
     * 2
     * >>> prime_fib(2)
     * 3
     * >>> prime_fib(3)
     * 5
     * >>> prime_fib(4)
     * 13
     * >>> prime_fib(5)
     * 89
     *
     */
    public static int primeFib(int n) {
		int a = 0;
		int b = 1;
		int c = 0;
		int count = 0;
		while (count < n) {
			c = a + b;
			a = b;
			b = c;
			if (isPrime(c)) {
				count += 1;
			}
		}
		return c;
    }

    public static boolean isPrime(int n) {
		if (n <= 1) {
			return false;
		}
		for (int i = 2; i < n; i++) {
			if (n % i == 0) {
				return false;
			}
		}
		return true;
	}

    public static void main(String[] args) {
        Scanner sc = new Scanner(System.in);
        int n = sc.nextInt();
        System.out.println(primeFib(n));
    }
