     * False
     *
     */
    public static Boolean sameChars(String s0, String s1) {
		if (s0.length()!= s1.length()) {
			return false;
		}
		int[] counts = new int[256];
		for (int i = 0; i < s0.length(); i++) {
			counts[s0.charAt(i)]++;
		}
		for (int i = 0; i < s1.length(); i++) {
			counts[s1.charAt(i)]--;
		}
		for (int i = 0; i < counts.length; i++) {
			if (counts[i]!= 0) {
				return false;
			}
		}
		return true;
    }
}
