     * [3, 5, 7]
     *
     */
    public static List<Integer> makeAPile(int n) {
		List<Integer> result = new ArrayList<Integer>();
		int current = n;
		while (current > 0) {
			result.add(current);
			if (current % 2 == 0) {
				current += 2;
			} else {
				current += 1;
			}
		}
		return result;
    }
