    public static List<Object> uniqueDigits(List<Integer> x) {
		List<Object> result = new ArrayList<Object>();
		for(int i=0;i<x.size();i++)
		{
			int num=x.get(i);
			while(num>0)
			{
				int digit=num%10;
				if(digit%2==0)
				{
					break;
				}
				num=num/10;
			}
			if(num==0)
			{
				result.add(x.get(i));
			}
		}
		Collections.sort(result);
		return result;
    }
}
