    public static Integer prodSigns(List<Object> arr) {
		if (arr.size() == 0) {
			return null;
		}
		int prod = 1;
		int sum = 0;
		for (Object num : arr) {
			if (num instanceof Integer) {
				if ((int) num == 0) {
					prod = 0;
				} else if ((int) num < 0) {
					prod *= -1;
				}
				sum += Math.abs((int) num);
			}
		}
		return prod * sum;
    }
