    public static List<Integer> divisors(int n) {
        List<Integer> divisors = new ArrayList<>();
        for(int i = 2; i <= Math.sqrt(n); i++) {
            if(n % i == 0) {
                divisors.add(i);
                if(i!= n/i) divisors.add(n/i);
            }
        }
        return divisors;
