def magic_square_test(my_matrix):
		"""
		This function takes a matrix as an argument and returns True if the matrix is a magic square, otherwise it returns False.
		"""
		if len(my_matrix)!= len(my_matrix[0]):
				return False
		n = len(my_matrix)
		magic_constant = n * (n**2 + 1) / 2
		for row in my_matrix:
				if sum(row)!= magic_constant:
						return False
		for col in zip(*my_matrix):
				if sum(col)!= magic_constant:
						return False
		diagonals = [sum(my_matrix[i][i] for i in range(n)), sum(my_matrix[i][n - i - 1] for i in range(n))]
		if sum(diagonals)!= magic_constant:
				return False
		return True

def main():
