"""
Implement a linked list.
"""
class Node:
    def __init__(self, data):
        self.data = data
        self.next = None

class LinkedList:
    def __init__(self):
        self.head = None

    def insert(self, data):
        new_node = Node(data)
        new_node.next = self.head
        self.head = new_node

    def search(self, key):
        current = self.head
        while current is not None:
            if current.data == key:
                return current
            current = current.next
        return None

    def delete(self, key):
        current = self.head
        previous = None
        while current is not None:
            if current.data == key:
                if previous is None:
                    self.head = current.next
                else:
                    previous.next = current.next
                return True
            previous = current
            current = current.next
        return False

    def print_list(self):
        current = self.head
        while current is not None:
            print(current.data)
            current = current.next

