"""
def check_subset_list(list1, list2): 

    # If list1 is empty then it is subset of list2. 
    if not list1: 
        return True

    # If list2 is empty then it is not subset of list2. 
    if not list2: 
        return False

    # Traverse list2 and compare each element with list1. 
    for i in range(len(list2)): 
        if list2[i] == list1[0] and check_subset_list(list1[1:], list2[i+1:]): 
            return True

