class Stack:
    def __init__(self):
        self.items = []

    def push(self, item):
        self.items.append(item)

    def pop(self):
        if self.isEmpty():
            return "Stack is empty"
        return self.items.pop()

    def isEmpty(self):
        return len(self.items) == 0

    def size(self):
        return len(self.items)

    def top(self):
        if self.isEmpty():
            return "Stack is empty"
        return self.items[-1]

    def display(self):
        if self.isEmpty():
            return "Stack is empty"
        for i in range(len(self.items)-1, -1, -1):
            print(self.items[i])
