     */
    public static Double meanAbsoluteDeviation(List<Double> numbers) {
		Double mean = Mean.mean(numbers);
		Double sum = 0.0;
		for (Double number : numbers) {
			sum += Math.abs(number - mean);
		}
		return sum / numbers.size();
    }
}
