public class SockMerchant {

    static int sockMerchant(int n, int[] ar) {
        // Complete this function
        int count = 0;
        int[] temp = new int[n];
        for(int i=0;i<n;i++){
            if(temp[i] == 0){
                for(int j=i+1;j<n;j++){
                    if(ar[i] == ar[j]){
                        count++;
                        temp[j] = 1;
                    }
                }
            }
        }
        return count;
    }

