		int count = 0;
		for (int i = 0; i < nums.size(); i++) {
			if (nums.get(i) instanceof Integer) {
				int num = (int) nums.get(i);
				if (num > 10 && num % 2 == 1 && num / 10 % 2 == 1) {
					count++;
				}
			}
		}
		return count;
	}
